    fn gcd(a: &BigInt, b: &BigInt) -> BigInt {
        assert!(a > &BigInt::from(0) && b > &BigInt::from(0));
        let mut a = a.clone();
        let mut b = b.clone();
        while b != BigInt::from(0) {
            (a, b) = (b.clone(), a % b);
        }
        a
    }

    fn to_words_le(bigint: &BigInt) -> Vec<u64> {
        let (sign, words) = bigint.to_u64_digits();
        assert!(sign != num_bigint::Sign::Minus);
        words
    }
